<?phpclass Access{	protected $accessContent = false;		protected function check_access($array,$userAccess,$index=0)	{		if(isset($array[$index]))		{			if($array[$index] == $userAccess)				$access = true;			else				$access = $this->check_access($array,$userAccess,$index + 1);		}				else			$access = false;					return $access;	}		public function set($set ='all')	{		if(!is_array($set))			$set = array($set);						$this->accessContent = true; //$this->check_access($set,$this->user_login_level)			}}